INSERT INTO Participants(ParticipantName,ParticipantTitle,ParticipantBio,ImageURL,IsActive) 
VALUES(N'o. Tomasz Oleniacz', N'Rektor Sanktuarium i Superior jezuitów w Chicago',
N'Pochodząc z jezuickiej parafii w Nowym Sączu z Towarzystwem Jezusowym jestem związany od zawsze, choć formalnie do nowicjatu wstąpiłem w 1987 roku. 
